mapper.update:
update ap_invoice
invoice_date=#{invoice_date}, vendor_id=#{vendor_id}, invoice_num=#{invoice_num}, invoice_amount=#{invoice_amount}, amount_paid=#{amount_paid}, description=#{description}, invoice_type=#{invoice_type}, payment_status=#{payment_status}, create_date=#{create_date}, create_by=#{create_by}, update_date=#{update_date}, update_by=#{update_by},
where invoice_id=#{invoice_id}

mapper.select:
SELECT ai.*,mv.vendor_name from ap_invoice ai
LEFT JOIN mdm_vendors mv on ai.vendor_id = mv.vendor_id
where 1=1
and ai.po_date >= #{po_date_start}
and ai.po_date <= #{po_date_end}
  and ai.vendor_id=#{vendor_id} 
  and ai.invoice_num like '%${invoice_num}%' 
 and ai.invoice_amount like '%${invoice_amount}%' 
 and ai.description like '%${description}%' 
  and ai.invoice_type=#{invoice_type} 
 and ai.payment_status like '%${payment_status}%' 
and ai.create_date >= #{create_date_start}
and ai.create_date <= #{create_date_end}\
  and ai.create_by=#{create_by} 
 and ai.update_date >= #{update_date_start} 
 and ai.update_date <= #{update_date_end} 
  and ai.update_by=#{update_by} 
ORDER BY update_date desc

SELECT ai.*,mv.vendor_name from ap_invoice ai
LEFT JOIN mdm_vendors mv on ai.vendor_id = mv.vendor_id
where ai.invoice_id = #{invoice_id}
