mapper.update:
update inv_item_onhand
location_id=#{location_id}, on_hand_quantity=#{on_hand_quantity}, price=#{price}, amount=#{amount}, min=#{min}, max=#{max},
where item_id=#{item_id} and org_id=#{org_id}

mapper.select:
SELECT * FROM inv_item_onhand where 1=1
  and item_id=#{item_id} 
  and org_id=#{org_id}

SELECT * FROM inv_item_onhand where 1=1
  and item_id=#{item_id} 
  and org_id=#{org_id} 
  and location_id=#{location_id} 
  and on_hand_quantity=#{on_hand_quantity} 
  and price=#{price} 
  and amount=#{amount} 
  and min=#{min} 
  and max=#{max}

select count(1) from (SELECT count(1) as mCount FROM inv_item_onhand iio
GROUP BY iio.org_id) temp
limit #{startIndex},#{perPage}

SELECT count(1) FROM inv_item_onhand iio
LEFT JOIN fnd_org fo on fo.org_id = iio.org_id
where 1=1
  and iio.item_id=#{item_id} 
  and iio.org_id=#{org_id} 
  and iio.location_id=#{location_id} 
  and iio.on_hand_quantity=#{on_hand_quantity} 
  and iio.price=#{price} 
  and iio.amount=#{amount} 
  and iio.min=#{min} 
  and iio.max=#{max}

SELECT iio.*, fo.org_name , mi.item_description,mi.uom FROM inv_item_onhand iio
LEFT JOIN fnd_org fo on fo.org_id = iio.org_id
LEFT JOIN mdm_item mi on iio.item_id = mi.item_id
where 1=1
  and iio.item_id=#{item_id} 
  and iio.org_id=#{org_id} 
  and iio.location_id=#{location_id} 
  and iio.on_hand_quantity=#{on_hand_quantity} 
  and iio.price=#{price} 
  and iio.amount=#{amount} 
  and iio.min=#{min} 
  and iio.max=#{max} 

ORDER BY iio.item_id desc
limit #{startIndex},#{perPage}
