mapper.select:
SELECT count(1) FROM inv_bill_header
where 1=1
  and bill_type=#{bill_type} 


  and operator=#{operator} 
  and target_operator=#{target_operator}

mapper.update:
update inv_bill_header
inv_org_id=#{inv_org_id}, target_inv_org_id=#{target_inv_org_id}, operator=#{operator}, target_operator=#{target_operator}, bill_date=#{bill_date}, remark=#{remark}, bill_status=#{bill_status}, source_system=#{source_system}, source_bill=#{source_bill}, ship_method=#{ship_method}, ship_corp=#{ship_corp}, ship_number=#{ship_number},
where bill_id=#{bill_id}
