mapper.update:
update pd_order_header
pd_header_name=#{pd_header_name}, manufactory_id=#{manufactory_id}, begin_date=#{begin_date}, end_date=#{end_date}, create_by=#{create_by}, create_date=#{create_date}, update_by=#{update_by}, update_date=#{update_date},
where pd_header_id=#{pd_header_id}

mapper.select:
SELECT poh.*, fu_create.USER_NAME as create_name, fu_update.USER_NAME as update_name FROM pd_order_header poh LEFT JOIN fnd_user fu_create on poh.create_by = fu_create._id -- 创建人 LEFT JOIN fnd_user fu_update on poh.update_by = fu_update._id -- 更新人 where 1=1 and poh.pd_header_name like '%${pd_header_name}%' and poh.po_type=#{po_type}
and ( fu_create.USER_NAME like '%${keyword}%' or fu_update.USER_NAME like '%${keyword}%' or poh.pd_header_name like '%${keyword}%' )
ORDER BY update_date desc limit #{startIndex},#{perPage}
