mapper.update:
update po_headers
po_type=#{po_type}, agent_id=#{agent_id}, vendor_id=#{vendor_id}, po_date=#{po_date}, bill_to_location=#{bill_to_location}, ship_to_location=#{ship_to_location}, status=#{status}, contract_code=#{contract_code}, contract_name=#{contract_name}, contract_file=#{contract_file}, comments=#{comments}, create_date=#{create_date}, create_by=#{create_by}, update_by=#{update_by}, update_date=#{update_date},
where po_header_id=#{po_header_id}

mapper.select:
SELECT count(1) FROM po_headers ph
LEFT JOIN fnd_user fu_agent on ph.agent_id = fu_agent._id -- 采购人
LEFT JOIN fnd_user fu_create on ph.create_by = fu_create._id -- 创建人
LEFT JOIN fnd_user fu_update on ph.update_by = fu_update._id -- 更新人
LEFT JOIN mdm_customers mc on ph.vendor_id = mc.customer_id -- 供应商
LEFT JOIN mdm_dict md on md.dict_code = 'order_po' -- 获取对应类型的数据字典
LEFT JOIN mdm_dict_value mdv on  md.dict_id =  mdv.dict_id and  ph.po_type = mdv.value_id -- 获取数据字典的值
where 1=1
  and ph.header_code like '%${header_code}%' 
  and ph.po_type=#{po_type} 
  and ph.agent_id=#{agent_id} 
  and ph.vendor_id=#{vendor_id} 
and ph.po_date >= #{po_date_start}
and ph.po_date <= #{po_date_end}
 and ph.bill_to_location like '%${bill_to_location}%' 
 and ph.ship_to_location like '%${ship_to_location}%' 
  and ph.status=#{status} 
 and ph.contract_code like '%${contract_code}%' 
 and ph.contract_name like '%${contract_name}%' 
 and ph.comments like '%${comments}%' 
and ph.create_date >= #{create_date_start}
and ph.create_date <= #{create_date_end}\
  and ph.create_by=#{create_by} 
 and ph.update_date >= #{update_date_start} 
 and ph.update_date <= #{update_date_end} 
  and ph.update_by=#{update_by} 


    and (
        ph.header_code like '%${keyword}%' or
        fu_agent.USER_NAME like '%${keyword}%' or
        fu_create.USER_NAME like '%${keyword}%' or
        fu_update.USER_NAME like '%${keyword}%' or
        mc.customer_name like '%${keyword}%' or
        mdv.value_name like '%${keyword}%' or
        ph.bill_to_location like '%${keyword}%' or
        ph.ship_to_location like '%${keyword}%'
    )

SELECT ph.*, fu_agent.USER_NAME as agent_name, fu_create.USER_NAME as create_name, fu_update.USER_NAME as update_name , mc.customer_name as vendor_name, mdv.value_name as po_type_name FROM po_headers ph LEFT JOIN fnd_user fu_agent on ph.agent_id = fu_agent._id -- 采购人 LEFT JOIN fnd_user fu_create on ph.create_by = fu_create._id -- 创建人 LEFT JOIN fnd_user fu_update on ph.update_by = fu_update._id -- 更新人 LEFT JOIN mdm_customers mc on ph.vendor_id = mc.customer_id -- 供应商 LEFT JOIN mdm_dict md on md.dict_code = 'order_po' -- 获取对应类型的数据字典 LEFT JOIN mdm_dict_value mdv on md.dict_id = mdv.dict_id and ph.po_type = mdv.value_id -- 获取数据字典的值 where 1=1 and ph.header_code like '%${header_code}%' and ph.po_type=#{po_type} and ph.agent_id=#{agent_id} and ph.vendor_id=#{vendor_id} and ph.po_date >= #{po_date_start} and ph.po_date <= #{po_date_end} and ph.bill_to_location like '%${bill_to_location}%' and ph.ship_to_location like '%${ship_to_location}%' and ph.status=#{status} and ph.contract_code like '%${contract_code}%' and ph.contract_name like '%${contract_name}%' and ph.comments like '%${comments}%' and ph.create_date >= #{create_date_start} and ph.create_date <= #{create_date_end}\ and ph.create_by=#{create_by} and ph.update_date >= #{update_date_start} and ph.update_date <= #{update_date_end} and ph.update_by=#{update_by}
and ( ph.header_code like '%${keyword}%' or fu_agent.USER_NAME like '%${keyword}%' or fu_create.USER_NAME like '%${keyword}%' or fu_update.USER_NAME like '%${keyword}%' or mc.customer_name like '%${keyword}%' or mdv.value_name like '%${keyword}%' or ph.bill_to_location like '%${keyword}%' or ph.ship_to_location like '%${keyword}%' )
ORDER BY update_date desc limit #{startIndex},#{perPage}
